CREATE TABLE activities (ACTIVITYID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, ACTIVITYDESCRIPTION INTEGER, ACTIVITYTITLE INTEGER, ACTIVITYFOR VARCHAR(255), CREATEDDATE DATETIME, MeasureDefId INTEGER, PRIMARY KEY (ACTIVITYID))
CREATE TABLE caregivers (CAREGIVERID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, FIRSTNAME VARCHAR(255), LASTNAME VARCHAR(255), MIDDLENAME VARCHAR(255), PRIMARY KEY (CAREGIVERID))
CREATE TABLE careperson (ID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, CREATEDDATE DATETIME, REMARK LONGTEXT, CareGiverId INTEGER, PersonId INTEGER, PRIMARY KEY (ID))
CREATE TABLE currentmeasure (CURRENTMEASUREID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, CREATEDDATE DATETIME, MEASUREDVALUE FLOAT, PRIMARY KEY (CURRENTMEASUREID))
CREATE TABLE measuredefinition (MEASUREDEFID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, DESCRIPTION LONGTEXT, MEASURENAME VARCHAR(255), UNITOF_MEASURE VARCHAR(30) NOT NULL, PRIMARY KEY (MEASUREDEFID))
CREATE TABLE measuredefinitionrange (RANGEID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, ENDVALUE FLOAT, RANGENAME VARCHAR(255), STARTVALUE FLOAT, MeasureDefId INTEGER, PRIMARY KEY (RANGEID))
CREATE TABLE measurehistory (HISTORYID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, CREATEDDATE DATETIME, DATE_REGISTERED DATETIME NOT NULL, MEASUREDVALUE FLOAT, PersonGoalId INTEGER, PRIMARY KEY (HISTORYID))
CREATE TABLE person (PERSONID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, BIRTHDAY DATE, FIRSTNAME VARCHAR(255), GENDER VARCHAR(255), LASTNAME VARCHAR(255), MIDDLENAME VARCHAR(255), PRIMARY KEY (PERSONID))
CREATE TABLE personalgoals (PERSONGOALID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, ACTIVITY_FREQUENCY INTEGER NOT NULL, CREATEDDATE DATETIME, GOALENDDATE DATETIME NOT NULL, GOALTOWARDS FLOAT, GOALTYPE VARCHAR(255), PersonId INTEGER, MeasureDefId INTEGER NOT NULL, PRIMARY KEY (PERSONGOALID))
CREATE TABLE reminders (REMINDERID INTEGER AUTO_INCREMENT NOT NULL UNIQUE, CREATEDDATE DATETIME, DESCRIPTION LONGTEXT, REMINDERTITLE VARCHAR(255), PersonId INTEGER, PRIMARY KEY (REMINDERID))
ALTER TABLE activities ADD CONSTRAINT FK_activities_MeasureDefId FOREIGN KEY (MeasureDefId) REFERENCES measuredefinition (MEASUREDEFID)
ALTER TABLE careperson ADD CONSTRAINT FK_careperson_CareGiverId FOREIGN KEY (CareGiverId) REFERENCES caregivers (CAREGIVERID)
ALTER TABLE careperson ADD CONSTRAINT FK_careperson_PersonId FOREIGN KEY (PersonId) REFERENCES person (PERSONID)
ALTER TABLE measuredefinitionrange ADD CONSTRAINT FK_measuredefinitionrange_MeasureDefId FOREIGN KEY (MeasureDefId) REFERENCES measuredefinition (MEASUREDEFID)
ALTER TABLE measurehistory ADD CONSTRAINT FK_measurehistory_PersonGoalId FOREIGN KEY (PersonGoalId) REFERENCES personalgoals (PERSONGOALID)
ALTER TABLE personalgoals ADD CONSTRAINT FK_personalgoals_PERSONGOALID FOREIGN KEY (PERSONGOALID) REFERENCES currentmeasure (PersonGoalId)
ALTER TABLE personalgoals ADD CONSTRAINT FK_personalgoals_PersonId FOREIGN KEY (PersonId) REFERENCES person (PERSONID)
ALTER TABLE personalgoals ADD CONSTRAINT FK_personalgoals_MeasureDefId FOREIGN KEY (MeasureDefId) REFERENCES measuredefinition (MEASUREDEFID)
